pragma solidity 0.8.13;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { FixedPointMathLib } from "@rari-capital/solmate/src/utils/FixedPointMathLib.sol";

import { InvalidParameter } from "./Auditor.sol";

contract InterestRateModel is AccessControl {
  using FixedPointMathLib for uint256;
  using FixedPointMathLib for int256;

  // Parameters to the system, expressed with 1e18 decimals
  uint256 public curveParameterA;
  int256 public curveParameterB;
  uint256 public maxUtilization;
  uint256 public fullUtilization;
  uint256 public spFeeRate;

  /// @notice Emitted when the curve parameters are changed by admin.
  /// @param a new curve parameter A.
  /// @param b new curve parameter B.
  /// @param maxUtilization new max utilization rate.
  /// @param fullUtilization new full utilization rate.
  event CurveParametersSet(uint256 a, int256 b, uint256 maxUtilization, uint256 fullUtilization);

  /// @notice Emitted when the spFeeRate parameter is changed by admin.
  /// @param spFeeRate rate charged to the mp suppliers to be accrued by the sp suppliers.
  event SpFeeRateSet(uint256 spFeeRate);

  constructor(
    uint256 curveParameterA_,
    int256 curveParameterB_,
    uint256 maxUtilization_,
    uint256 fullUtilization_,
    uint256 spFeeRate_
  ) {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

    setCurveParameters(curveParameterA_, curveParameterB_, maxUtilization_, fullUtilization_);
    setSPFeeRate(spFeeRate_);
  }

  /// @notice Sets the rate charged to the mp depositors that the sp suppliers will retain for initially providing
  /// liquidity.
  /// @dev Value can only be set between 20% and 0%.
  /// @param spFeeRate_ percentage amount represented with 1e18 decimals.
  function setSPFeeRate(uint256 spFeeRate_) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (spFeeRate_ > 0.2e18) revert InvalidParameter();
    spFeeRate = spFeeRate_;
    emit SpFeeRateSet(spFeeRate_);
  }

  /// @notice Gets this model's curve parameters.
  /// @return parameters (curveA, curveB, maxUtilization, fullUtilization).
  function getCurveParameters()
    external
    view
    returns (
      uint256,
      int256,
      uint256,
      uint256
    )
  {
    return (curveParameterA, curveParameterB, maxUtilization, fullUtilization);
  }

  /// @notice Calculates the amount of revenue sharing between the smart pool and the new MP supplier.
  /// @param suppliedSP amount of money currently being supplied in the fixed rate pool.
  /// @param unassignedEarnings earnings not yet accrued to the SP that should be shared with the current supplier.
  /// @param amount amount being provided by the MP supplier.
  /// @return earningsShare yield to be offered to the MP supplier.
  /// @return earningsShareSP yield to be accrued by the SP suppliers for initially providing the liquidity.
  function getYieldForDeposit(
    uint256 suppliedSP,
    uint256 unassignedEarnings,
    uint256 amount
  ) external view returns (uint256 earningsShare, uint256 earningsShareSP) {
    if (suppliedSP != 0) {
      // User can't make more fees after the total borrowed amount
      earningsShare = unassignedEarnings.mulDivDown(Math.min(amount, suppliedSP), suppliedSP);
      earningsShareSP = earningsShare.mulWadDown(spFeeRate);
      earningsShare -= earningsShareSP;
    }
  }

  /// @notice Updates this model's curve parameters.
  /// @dev FullUR can only be between 1 and 52. UMaxUR can only be higher than FullUR and at most 3 times FullUR.
  /// @param curveParameterA_ curve parameter A.
  /// @param curveParameterB_ curve parameter B.
  /// @param maxUtilization_ % of MP supp.
  /// @param fullUtilization_ full UR.
  function setCurveParameters(
    uint256 curveParameterA_,
    int256 curveParameterB_,
    uint256 maxUtilization_,
    uint256 fullUtilization_
  ) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (
      fullUtilization_ > 52e18 ||
      fullUtilization_ < 1e18 ||
      fullUtilization_ >= maxUtilization_ ||
      fullUtilization_ < maxUtilization_ / 3
    ) revert InvalidParameter();

    curveParameterA = curveParameterA_;
    curveParameterB = curveParameterB_;
    maxUtilization = maxUtilization_;
    fullUtilization = fullUtilization_;

    // reverts if it's an invalid curve (such as one yielding a negative interest rate).
    // doing it works because it's a monotonously increasing function.
    rate(0, 0);

    emit CurveParametersSet(curveParameterA_, curveParameterB_, maxUtilization_, fullUtilization_);
  }

  /// @notice Gets fee to borrow a certain amount at a certain maturity with supply/demand values in the fixed rate pool
  /// and supply/demand values in the smart pool.
  /// @param maturity maturity date for calculating days left to maturity.
  /// @param currentDate the current block timestamp. Received from caller for easier testing.
  /// @param amount the current borrow's amount.
  /// @param borrowedMP ex-ante amount borrowed from this maturity.
  /// @param suppliedMP deposits in the fixed rate pool.
  /// @param smartPoolAssetsAverage the average of the smart pool's assets.
  /// @return fee the borrower will have to pay, as a factor (1% interest is represented as the wad for 0.01 == 10^16).
  function getRateToBorrow(
    uint256 maturity,
    uint256 currentDate,
    uint256 amount,
    uint256 borrowedMP,
    uint256 suppliedMP,
    uint256 smartPoolAssetsAverage
  ) public view returns (uint256) {
    if (currentDate >= maturity) revert AlreadyMatured();

    uint256 supplied = suppliedMP + smartPoolAssetsAverage.divWadDown(fullUtilization);
    uint256 utilizationBefore = borrowedMP.divWadDown(supplied);
    uint256 utilizationAfter = (borrowedMP + amount).divWadDown(supplied);

    if (utilizationAfter > fullUtilization) revert UtilizationExceeded();

    return rate(utilizationBefore, utilizationAfter).mulDivDown(maturity - currentDate, 365 days);
  }

  /// @notice Returns the interest rate integral from `u0` to `u1`, using the analytical solution (ln).
  /// @dev handles special case where delta utilization tends to zero, using l'hôpital's rule.
  /// @param utilizationBefore ex-ante utilization rate, with 18 decimals precision.
  /// @param utilizationAfter ex-post utilization rate, with 18 decimals precision.
  /// @return the interest rate, with 18 decimals precision.
  function rate(uint256 utilizationBefore, uint256 utilizationAfter) internal view returns (uint256) {
    int256 r = int256(
      utilizationAfter - utilizationBefore < 2.5e9
        ? curveParameterA.divWadDown(maxUtilization - utilizationBefore)
        : curveParameterA.mulDivDown(
          uint256(int256((maxUtilization - utilizationBefore).divWadDown(maxUtilization - utilizationAfter)).lnWad()),
          utilizationAfter - utilizationBefore
        )
    ) + curveParameterB;
    assert(r >= 0);
    return uint256(r);
  }
}

error AlreadyMatured();
error InvalidAmount();
error UtilizationExceeded();
","keccak256":"0xd9b04798b55fe50b8903eb236dcc81168ee26063381bf2ba1c60756661911b7f","license":"GPL-3.0-or-later"},"contracts/utils/PoolLib.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.13;

import { FixedPointMathLib } from "@rari-capital/solmate/src/utils/FixedPointMathLib.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { TSUtils } from "./TSUtils.sol";

library PoolLib {
  using FixedPointMathLib for uint256;

  /// @notice contains the accountability of a fixed interest rate pool.
  /// @param borrowed total amount borrowed from the pool.
  /// @param supplied total amount supplied to the pool.
  /// @param earningsUnassigned total amount of earnings not yet distributed and accrued.
  /// @param lastAccrual timestamp for the last time that some earnings have been distributed to earningsSP.
  struct FixedPool {
    uint256 borrowed;
    uint256 supplied;
    uint256 earningsUnassigned;
    uint256 lastAccrual;
  }

  /// @notice contains principal and fee of a borrow or a supply position of a user in a fixed rate pool.
  /// @param principal amount borrowed or supplied to the fixed rate pool.
  /// @param fee amount of fees to be repaid or earned at the maturity of the fixed rate pool.
  struct Position {
    uint256 principal;
    uint256 fee;
  }

  /// @notice calculates the amount that a fixed rate pool borrowed from the smart pool.
  /// @param pool fixed rate pool.
  /// @return amount borrowed from the fixed rate pool.
  function smartPoolBorrowed(FixedPool storage pool) internal view returns (uint256) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    return borrowed - Math.min(borrowed, supplied);
  }

  /// @notice registers an operation to add supply to a fixed rate pool and potentially reduce smart pool debt.
  /// @param pool fixed rate pool where an amount will be added to the supply.
  /// @param amount amount to be added to the supply.
  /// @return smartPoolDebtReduction amount that will be reduced from the smart pool debt.
  function deposit(FixedPool storage pool, uint256 amount) internal returns (uint256 smartPoolDebtReduction) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    pool.supplied = supplied + amount;
    smartPoolDebtReduction = Math.min(borrowed - Math.min(borrowed, supplied), amount);
  }

  /// @notice registers an operation to reduce borrowed amount from a fixed rate pool
  /// and potentially reduce smart pool debt.
  /// @param pool fixed rate pool where an amount will be repaid.
  /// @param amount amount to be added to the fixed rate pool.
  /// @return smartPoolDebtReduction amount that will be reduced from the smart pool debt.
  function repay(FixedPool storage pool, uint256 amount) internal returns (uint256 smartPoolDebtReduction) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    pool.borrowed = borrowed - amount;
    smartPoolDebtReduction = Math.min(borrowed - Math.min(borrowed, supplied), amount);
  }

  /// @notice registers an operation to increase borrowed amount of a fixed rate pool
  ///  and potentially increase smart pool debt.
  /// @param pool fixed rate pool where an amount will be borrowed.
  /// @param amount amount to be borrowed from the fixed rate pool.
  /// @return smartPoolDebtAddition amount of new debt that needs to be borrowed from the smart pool.
  function borrow(
    FixedPool storage pool,
    uint256 amount,
    uint256 maxDebt
  ) internal returns (uint256 smartPoolDebtAddition) {
    uint256 borrowed = pool.borrowed;
    uint256 newBorrowed = borrowed + amount;
    uint256 oldSupply = Math.max(borrowed, pool.supplied);

    smartPoolDebtAddition = newBorrowed - Math.min(oldSupply, newBorrowed);

    if (smartPoolDebtAddition > maxDebt) revert InsufficientProtocolLiquidity();

    pool.borrowed = newBorrowed;
  }

  /// @notice registers an operation to reduce supply from a fixed rate pool and potentially increase smart pool debt.
  /// @param pool fixed rate pool where amount will be withdrawn.
  /// @param amountToDiscount amount to be withdrawn from the fixed rate pool.
  /// @return smartPoolDebtAddition amount of new debt that needs to be borrowed from the smart pool.
  function withdraw(
    FixedPool storage pool,
    uint256 amountToDiscount,
    uint256 maxDebt
  ) internal returns (uint256 smartPoolDebtAddition) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    uint256 newSupply = supplied - amountToDiscount;

    smartPoolDebtAddition = Math.min(supplied, borrowed) - Math.min(newSupply, borrowed);

    if (smartPoolDebtAddition > maxDebt) revert InsufficientProtocolLiquidity();

    pool.supplied = newSupply;
  }

  /// @notice accrues smart pool earnings from earningsUnassigned based on the lastAccrual time.
  /// @param pool fixed rate pool where earnings will be accrued.
  /// @param currentTimestamp timestamp of the current transaction.
  /// @param maturity maturity date of the pool.
  /// @return earningsSP amount of earnings to be distributed to the smart pool.
  function accrueEarnings(
    FixedPool storage pool,
    uint256 maturity,
    uint256 currentTimestamp
  ) internal returns (uint256 earningsSP) {
    uint256 lastAccrual = pool.lastAccrual;

    if (lastAccrual == maturity) return 0;

    // seconds from last accrual to the closest:
    // maturity date or the current timestamp
    uint256 secondsSinceLastAccrue = TSUtils.secondsPre(lastAccrual, Math.min(maturity, currentTimestamp));
    // seconds from last accrual to the maturity date
    uint256 secondsTotalToMaturity = TSUtils.secondsPre(lastAccrual, maturity);
    pool.lastAccrual = Math.min(maturity, currentTimestamp);

    // assign some of the earnings to be collected at maturity
    uint256 earningsUnassigned = pool.earningsUnassigned;
    earningsSP = earningsUnassigned.mulDivDown(secondsSinceLastAccrue, secondsTotalToMaturity);
    pool.earningsUnassigned = earningsUnassigned - earningsSP;
  }

  /// @notice modify positions based on a certain amount, keeping the original principal/fee ratio.
  /// @dev modifies the original struct and returns it. Needs for the amount to be less than the principal and the fee
  /// @param position original position to be scaled.
  /// @param amount to be used as a full value (principal + interest).
  /// @return Position scaled position.
  function scaleProportionally(Position memory position, uint256 amount) internal pure returns (Position memory) {
    uint256 principal = amount.mulDivDown(position.principal, position.principal + position.fee);
    position.principal = principal;
    position.fee = amount - principal;
    return position;
  }

  /// @notice reduce positions based on a certain amount, keeping the original principal/fee ratio.
  /// @dev modifies the original struct and returns it.
  /// @param position original position to be reduced.
  /// @param amount to be used as a full value (principal + interest).
  /// @return Position reduced position.
  function reduceProportionally(Position memory position, uint256 amount) internal pure returns (Position memory) {
    uint256 principal = amount.mulDivDown(position.principal, position.principal + position.fee);
    position.principal -= principal;
    position.fee -= amount - principal;
    return position;
  }

  /// @notice calculates what proportion of earnings would amountFunded represent considering suppliedSP.
  /// @param earnings amount to be distributed.
  /// @param suppliedSP amount that the fixed rate pool borrowed from the smart pool.
  /// @param amountFunded amount that will be checked if came from the smart pool or fixed rate pool.
  /// @return earningsUnassigned earnings to be added to earningsUnassigned.
  /// @return earningsSP earnings to be distributed to the smart pool.
  function distributeEarningsAccordingly(
    uint256 earnings,
    uint256 suppliedSP,
    uint256 amountFunded
  ) internal pure returns (uint256 earningsUnassigned, uint256 earningsSP) {
    earningsSP = amountFunded == 0
      ? 0
      : earnings.mulDivDown(amountFunded - Math.min(suppliedSP, amountFunded), amountFunded);
    earningsUnassigned = earnings - earningsSP;
  }

  /// @notice adds a maturity date to the borrow or supply positions of the user.
  /// @param encoded encoded maturity dates where the user borrowed or supplied to.
  /// @param maturity the new maturity where the user will borrow or supply to.
  /// @return updated encoded maturity dates.
  function setMaturity(uint256 encoded, uint256 maturity) internal pure returns (uint256) {
    // we initialize the maturity date with also the 1st bit on the 33th position set
    if (encoded == 0) return maturity | (1 << 32);

    uint256 baseMaturity = encoded % (1 << 32);
    if (maturity < baseMaturity) {
      // If the new maturity date is lower than the base, then we need to set it as the new base. We wipe clean the
      // last 32 bits, we shift the amount of INTERVALS and we set the new value with the 33rd bit set
      uint256 range = (baseMaturity - maturity) / TSUtils.INTERVAL;
      if (encoded >> (256 - range) != 0) revert MaturityOverflow();
      encoded = ((encoded >> 32) << (32 + range));
      return maturity | encoded | (1 << 32);
    } else {
      uint256 range = (maturity - baseMaturity) / TSUtils.INTERVAL;
      if (range > 223) revert MaturityOverflow();
      return encoded | (1 << (32 + range));
    }
  }

  /// @notice remove maturity from user's borrow or supplied positions.
  /// @param encoded encoded maturity dates where the user borrowed or supplied to.
  /// @param maturity maturity date to be removed.
  /// @return updated encoded maturity dates.
  function clearMaturity(uint256 encoded, uint256 maturity) internal pure returns (uint256) {
    if (encoded == 0 || encoded == maturity | (1 << 32)) return 0;

    uint256 baseMaturity = encoded % (1 << 32);
    // if the baseMaturity is the one being cleaned
    if (maturity == baseMaturity) {
      // We're wiping 32 bytes + 1 for the old base flag
      uint256 packed = encoded >> 33;
      uint256 range = 1;
      while ((packed & 1) == 0 && packed != 0) {
        unchecked {
          ++range;
        }
        packed >>= 1;
      }
      encoded = ((encoded >> (32 + range)) << 32);
      return (maturity + (range * TSUtils.INTERVAL)) | encoded;
    } else {
      // otherwise just clear the bit
      return encoded & ~(1 << (32 + ((maturity - baseMaturity) / TSUtils.INTERVAL)));
    }
  }

  /// @notice checks if the user has positions in a maturity date.
  /// @param encoded encoded maturity dates where the user borrowed or supplied to.
  /// @param maturity maturity date.
  /// @return true if the user has positions in the maturity date.
  function hasMaturity(uint256 encoded, uint256 maturity) internal pure returns (bool) {
    uint256 baseMaturity = encoded % (1 << 32);
    if (maturity < baseMaturity) return false;

    uint256 range = (maturity - baseMaturity) / TSUtils.INTERVAL;
    if (range > 223) return false;
    return ((encoded >> 32) & (1 << range)) != 0;
  }
}

error InsufficientProtocolLiquidity();
error MaturityOverflow();
","keccak256":"0xceb42fc4343b439db74bfa8a2b8b190bff90add91526e3b0fe47eee93ea9a3d7","license":"GPL-3.0-or-later"},"contracts/utils/TSUtils.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.13;

library TSUtils {
  enum State {
    NONE,
    INVALID,
    MATURED,
    VALID,
    NOT_READY
  }

  uint32 public constant INTERVAL = 4 weeks;

  /// @notice calculates how many seconds are left to a certain date.
  /// @param timestampFrom to calculate the difference in seconds from a date.
  /// @param timestampTo to calculate the difference in seconds to a date.
  /// @return seconds left to the date.
  function secondsPre(uint256 timestampFrom, uint256 timestampTo) internal pure returns (uint256) {
    return timestampFrom < timestampTo ? timestampTo - timestampFrom : 0;
  }

  /// @notice returns a pool `time` state based on the current time, maxPools available, and INTERVAL.
  /// @param timestamp timestamp of the current time.
  /// @param maturity used as maturity date / pool id.
  /// @param maxPools number of pools available in the time horizon.
  /// @return state: if a pool is VALID, not yet available(NOT_READY), INVALID or MATURED.
  function getPoolState(
    uint256 timestamp,
    uint256 maturity,
    uint8 maxPools
  ) private pure returns (State) {
    if (maturity % INTERVAL != 0) return State.INVALID;

    if (maturity <= timestamp) return State.MATURED;

    if (maturity > timestamp - (timestamp % INTERVAL) + (INTERVAL * maxPools)) return State.NOT_READY;

    return State.VALID;
  }

  /// @notice verifies that a maturity is VALID, MATURED, NOT_READY or INVALID.
  /// @dev if expected state doesn't match the calculated one, it reverts with a custom error "UnmatchedPoolState".
  /// @param maturity timestamp of the maturity date to be verified.
  /// @param requiredState state required by the caller to be verified (see TSUtils.State() for description).
  /// @param alternativeState state required by the caller to be verified (see TSUtils.State() for description).
  function validateRequiredPoolState(
    uint8 maxFuturePools,
    uint256 maturity,
    State requiredState,
    State alternativeState
  ) internal view {
    State poolState = getPoolState(block.timestamp, maturity, maxFuturePools);

    if (poolState != requiredState && poolState != alternativeState) {
      if (alternativeState == State.NONE) revert UnmatchedPoolState(uint8(poolState), uint8(requiredState));

      revert UnmatchedPoolStateMultiple(uint8(poolState), uint8(requiredState), uint8(alternativeState));
    }
  }
}

error UnmatchedPoolState(uint8 state, uint8 requiredState);
error UnmatchedPoolStateMultiple(uint8 state, uint8 requiredState, uint8 alternativeState);
