pragma solidity 0.8.13;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { FixedPointMathLib } from "@rari-capital/solmate/src/utils/FixedPointMathLib.sol";

import { InvalidParameter } from "./Auditor.sol";
import { PoolLib } from "./utils/PoolLib.sol";

contract InterestRateModel is AccessControl {
  using PoolLib for PoolLib.MaturityPool;
  using FixedPointMathLib for uint256;
  using FixedPointMathLib for int256;

  // Parameters to the system, expressed with 1e18 decimals
  uint256 public curveParameterA;
  int256 public curveParameterB;
  uint256 public maxUtilization;
  uint256 public fullUtilization;
  uint256 public spFeeRate;

  /// @notice Emitted when the curve parameters are changed by admin.
  /// @param a new curve parameter A.
  /// @param b new curve parameter B.
  /// @param maxUtilization new max utilization rate.
  /// @param fullUtilization new full utilization rate.
  event CurveParametersSet(uint256 a, int256 b, uint256 maxUtilization, uint256 fullUtilization);

  /// @notice Emitted when the spFeeRate parameter is changed by admin.
  /// @param spFeeRate rate charged to the mp suppliers to be accrued by the sp suppliers.
  event SpFeeRateSet(uint256 spFeeRate);

  constructor(
    uint256 curveParameterA_,
    int256 curveParameterB_,
    uint256 maxUtilization_,
    uint256 fullUtilization_,
    uint256 spFeeRate_
  ) {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

    setCurveParameters(curveParameterA_, curveParameterB_, maxUtilization_, fullUtilization_);
    setSPFeeRate(spFeeRate_);
  }

  /// @notice Sets the rate charged to the mp depositors that the sp suppliers will retain for initially providing
  /// liquidity.
  /// @dev Value can only be set between 20% and 0%.
  /// @param spFeeRate_ percentage amount represented with 1e18 decimals.
  function setSPFeeRate(uint256 spFeeRate_) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (spFeeRate_ > 0.2e18) revert InvalidParameter();
    spFeeRate = spFeeRate_;
    emit SpFeeRateSet(spFeeRate_);
  }

  /// @notice Gets this model's curve parameters.
  /// @return parameters (curveA, curveB, maxUtilization, fullUtilization).
  function getCurveParameters()
    external
    view
    returns (
      uint256,
      int256,
      uint256,
      uint256
    )
  {
    return (curveParameterA, curveParameterB, maxUtilization, fullUtilization);
  }

  /// @notice Calculates the amount of revenue sharing between the smart pool and the new MP supplier.
  /// @param suppliedSP amount of money currently being supplied in the maturity pool.
  /// @param unassignedEarnings earnings not yet accrued to the SP that should be shared with the current supplier.
  /// @param amount amount being provided by the MP supplier.
  /// @return earningsShare yield to be offered to the MP supplier.
  /// @return earningsShareSP yield to be accrued by the SP suppliers for initially providing the liquidity.
  function getYieldForDeposit(
    uint256 suppliedSP,
    uint256 unassignedEarnings,
    uint256 amount
  ) external view returns (uint256 earningsShare, uint256 earningsShareSP) {
    if (suppliedSP != 0) {
      // User can't make more fees after the total borrowed amount
      earningsShare = unassignedEarnings.mulDivDown(Math.min(amount, suppliedSP), suppliedSP);
      earningsShareSP = earningsShare.mulWadDown(spFeeRate);
      earningsShare -= earningsShareSP;
    }
  }

  /// @notice Updates this model's curve parameters.
  /// @dev FullUR can only be between 1 and 52. UMaxUR can only be higher than FullUR and at most 3 times FullUR.
  /// @param curveParameterA_ curve parameter A.
  /// @param curveParameterB_ curve parameter B.
  /// @param maxUtilization_ % of MP supp.
  /// @param fullUtilization_ full UR.
  function setCurveParameters(
    uint256 curveParameterA_,
    int256 curveParameterB_,
    uint256 maxUtilization_,
    uint256 fullUtilization_
  ) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (
      fullUtilization_ > 52e18 ||
      fullUtilization_ < 1e18 ||
      fullUtilization_ >= maxUtilization_ ||
      fullUtilization_ < maxUtilization_ / 3
    ) revert InvalidParameter();

    curveParameterA = curveParameterA_;
    curveParameterB = curveParameterB_;
    maxUtilization = maxUtilization_;
    fullUtilization = fullUtilization_;

    // reverts if it's an invalid curve (such as one yielding a negative interest rate).
    // doing it works because it's a monotonously increasing function.
    rate(0, 0);

    emit CurveParametersSet(curveParameterA_, curveParameterB_, maxUtilization_, fullUtilization_);
  }

  /// @notice Gets fee to borrow a certain amount in a certain maturity with supply/demand values in the maturity pool
  /// and supply/demand values in the smart pool.
  /// @param maturity maturity date for calculating days left to maturity.
  /// @param currentDate the current block timestamp. Received from caller for easier testing.
  /// @param amount the current borrow's amount.
  /// @param borrowedMP ex-ante amount borrowed from this maturity.
  /// @param suppliedMP deposits in maturity pool.
  /// @param smartPoolAssetsAverage the average of the smart pool's assets.
  /// @return fee the borrower will have to pay, as a factor (1% interest is represented as the wad for 0.01 == 10^16).
  function getRateToBorrow(
    uint256 maturity,
    uint256 currentDate,
    uint256 amount,
    uint256 borrowedMP,
    uint256 suppliedMP,
    uint256 smartPoolAssetsAverage
  ) public view returns (uint256) {
    if (currentDate >= maturity) revert AlreadyMatured();

    uint256 supplied = suppliedMP + smartPoolAssetsAverage.divWadDown(fullUtilization);
    uint256 utilizationBefore = borrowedMP.divWadDown(supplied);
    uint256 utilizationAfter = (borrowedMP + amount).divWadDown(supplied);

    if (utilizationAfter > fullUtilization) revert UtilizationExceeded();

    return rate(utilizationBefore, utilizationAfter).mulDivDown(maturity - currentDate, 365 days);
  }

  /// @notice Returns the interest rate integral from `u0` to `u1`, using the analytical solution (ln).
  /// @dev handles special case where delta utilization tends to zero, using l'hôpital's rule.
  /// @param utilizationBefore ex-ante utilization rate, with 18 decimals precision.
  /// @param utilizationAfter ex-post utilization rate, with 18 decimals precision.
  /// @return the interest rate, with 18 decimals precision.
  function rate(uint256 utilizationBefore, uint256 utilizationAfter) internal view returns (uint256) {
    int256 r = int256(
      utilizationAfter - utilizationBefore < 2.5e9
        ? curveParameterA.divWadDown(maxUtilization - utilizationBefore)
        : curveParameterA.mulDivDown(
          uint256(int256((maxUtilization - utilizationBefore).divWadDown(maxUtilization - utilizationAfter)).lnWad()),
          utilizationAfter - utilizationBefore
        )
    ) + curveParameterB;
    assert(r >= 0);
    return uint256(r);
  }
}

error AlreadyMatured();
error InvalidAmount();
error UtilizationExceeded();
","keccak256":"0xa23ff07be56a5b1dda646c1bf1f275b68df98ae237fb2184200fbdc8eec52159","license":"GPL-3.0-or-later"},"contracts/PoolAccounting.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.13;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { FixedPointMathLib } from "@rari-capital/solmate/src/utils/FixedPointMathLib.sol";
import { InterestRateModel } from "./InterestRateModel.sol";
import { InvalidParameter } from "./Auditor.sol";
import { TSUtils } from "./utils/TSUtils.sol";
import { PoolLib } from "./utils/PoolLib.sol";

abstract contract PoolAccounting is AccessControl {
  using PoolLib for PoolLib.MaturityPool;
  using PoolLib for PoolLib.Position;
  using PoolLib for uint256;
  using FixedPointMathLib for uint256;
  using FixedPointMathLib for int256;

  // Vars used in `borrowMP` to avoid stack too deep problem
  struct BorrowVars {
    PoolLib.Position position;
    uint256 fee;
    uint256 newUnassignedEarnings;
    uint256 earningsSP;
  }

  struct DampSpeed {
    uint256 up;
    uint256 down;
  }

  mapping(uint256 => mapping(address => PoolLib.Position)) public mpUserSuppliedAmount;
  mapping(uint256 => mapping(address => PoolLib.Position)) public mpUserBorrowedAmount;

  mapping(address => uint256) public userMpBorrowed;
  mapping(address => uint256) public userMpSupplied;
  mapping(uint256 => PoolLib.MaturityPool) public maturityPools;
  uint256 public smartPoolBorrowed;
  uint256 public smartPoolEarningsAccumulator;
  uint256 public smartPoolAssetsAverage;
  uint32 public lastAverageUpdate;

  InterestRateModel public interestRateModel;

  uint256 public penaltyRate;
  uint256 public smartPoolReserveFactor;
  uint256 public dampSpeedUp;
  uint256 public dampSpeedDown;

  /// @notice emitted when the interestRateModel is changed by admin.
  /// @param newInterestRateModel new interest rate model to be used by this PoolAccounting.
  event InterestRateModelSet(InterestRateModel indexed newInterestRateModel);

  /// @notice emitted when the penaltyRate is changed by admin.
  /// @param newPenaltyRate penaltyRate percentage per second represented with 1e18 decimals.
  event PenaltyRateSet(uint256 newPenaltyRate);

  /// @notice emitted when the smartPoolReserveFactor is changed by admin.
  /// @param newSmartPoolReserveFactor smartPoolReserveFactor percentage.
  event SmartPoolReserveFactorSet(uint256 newSmartPoolReserveFactor);

  /// @notice emitted when the damp speeds are changed by admin.
  /// @param newDampSpeedUp represented with 1e18 decimals.
  /// @param newDampSpeedDown represented with 1e18 decimals.
  event DampSpeedSet(uint256 newDampSpeedUp, uint256 newDampSpeedDown);

  constructor(
    InterestRateModel interestRateModel_,
    uint256 penaltyRate_,
    uint256 smartPoolReserveFactor_,
    DampSpeed memory dampSpeed
  ) {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    setInterestRateModel(interestRateModel_);
    setPenaltyRate(penaltyRate_);
    setSmartPoolReserveFactor(smartPoolReserveFactor_);
    setDampSpeed(dampSpeed);
  }

  /// @notice Sets the interest rate model to be used by this PoolAccounting.
  /// @param interestRateModel_ new interest rate model.
  function setInterestRateModel(InterestRateModel interestRateModel_) public onlyRole(DEFAULT_ADMIN_ROLE) {
    interestRateModel = interestRateModel_;
    emit InterestRateModelSet(interestRateModel_);
  }

  /// @notice Sets the penalty rate per second.
  /// @dev Value can only be set approximately between 5% and 1% daily.
  /// @param penaltyRate_ percentage represented with 18 decimals.
  function setPenaltyRate(uint256 penaltyRate_) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (penaltyRate_ > 5.79e11 || penaltyRate_ < 1.15e11) revert InvalidParameter();
    penaltyRate = penaltyRate_;
    emit PenaltyRateSet(penaltyRate_);
  }

  /// @notice Sets the percentage that represents the smart pool liquidity reserves that can't be borrowed.
  /// @dev Value can only be set between 20% and 0%.
  /// @param smartPoolReserveFactor_ parameter represented with 18 decimals.
  function setSmartPoolReserveFactor(uint256 smartPoolReserveFactor_) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (smartPoolReserveFactor_ > 0.2e18) revert InvalidParameter();
    smartPoolReserveFactor = smartPoolReserveFactor_;
    emit SmartPoolReserveFactorSet(smartPoolReserveFactor_);
  }

  /// @notice Sets the damp speed used to update the smartPoolAssetsAverage.
  /// @dev Values can only be set between 0 and 100%.
  /// @param dampSpeed represented with 18 decimals.
  function setDampSpeed(DampSpeed memory dampSpeed) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (dampSpeed.up > 1e18 || dampSpeed.down > 1e18) revert InvalidParameter();
    dampSpeedUp = dampSpeed.up;
    dampSpeedDown = dampSpeed.down;
    emit DampSpeedSet(dampSpeed.up, dampSpeed.down);
  }

  /// @notice Accounts for borrowing from a maturity pool.
  /// @dev It doesn't check liquidity for the borrower.
  /// The `fixedLender` should call `validateBorrowMP` immediately after calling this function.
  /// @param maturity maturity date / pool id where the assets will be borrowed.
  /// @param borrower borrower that will take the debt.
  /// @param amount amount that the borrower will be borrowing.
  /// @param maxAmountAllowed maximum amount that the borrower is willing to pay at maturity.
  /// @param smartPoolAssets total assets in the smart pool.
  /// @return totalOwedNewBorrow total amount that will need to be paid at maturity for this borrow.
  /// @return earningsSP amount of earnings to be accrued by the smart pool.
  function borrowMP(
    uint256 maturity,
    address borrower,
    uint256 amount,
    uint256 maxAmountAllowed,
    uint256 smartPoolAssets
  ) internal returns (uint256 totalOwedNewBorrow, uint256 earningsSP) {
    BorrowVars memory borrowVars;
    PoolLib.MaturityPool storage pool = maturityPools[maturity];

    earningsSP = pool.accrueEarnings(maturity, block.timestamp);

    updateSmartPoolAssetsAverage(smartPoolAssets);
    borrowVars.fee = amount.mulWadDown(
      interestRateModel.getRateToBorrow(
        maturity,
        block.timestamp,
        amount,
        pool.borrowed,
        pool.supplied,
        smartPoolAssetsAverage
      )
    );
    totalOwedNewBorrow = amount + borrowVars.fee;

    uint256 memSPBorrowed = smartPoolBorrowed;
    memSPBorrowed = memSPBorrowed + pool.borrow(amount, smartPoolAssets - memSPBorrowed);
    smartPoolBorrowed = memSPBorrowed;
    if (memSPBorrowed > smartPoolAssets.mulWadDown(1e18 - smartPoolReserveFactor)) {
      revert SmartPoolReserveExceeded();
    }
    // We validate that the user is not taking arbitrary fees
    if (totalOwedNewBorrow > maxAmountAllowed) revert TooMuchSlippage();

    // If user doesn't have a current position, we add it to the list of all of them
    borrowVars.position = mpUserBorrowedAmount[maturity][borrower];
    if (borrowVars.position.principal == 0) {
      userMpBorrowed[borrower] = userMpBorrowed[borrower].setMaturity(maturity);
    }

    // We calculate what portion of the fees are to be accrued and what portion goes to earnings accumulator
    (borrowVars.newUnassignedEarnings, borrowVars.earningsSP) = PoolLib.distributeEarningsAccordingly(
      borrowVars.fee,
      pool.smartPoolBorrowed(),
      amount
    );
    smartPoolEarningsAccumulator += borrowVars.earningsSP;
    pool.earningsUnassigned += borrowVars.newUnassignedEarnings;

    mpUserBorrowedAmount[maturity][borrower] = PoolLib.Position(
      borrowVars.position.principal + amount,
      borrowVars.position.fee + borrowVars.fee
    );
  }

  /// @notice Accounts for depositing to a maturity pool.
  /// @param maturity maturity date / pool id where the assets will be deposited.
  /// @param supplier address that will be depositing the assets.
  /// @param amount amount that the supplier will be depositing.
  /// @param minAmountRequired minimum amount that the supplier is expecting to receive at maturity.
  /// @return currentTotalDeposit the amount that should be collected at maturity for this deposit.
  /// @return earningsSP amount of earnings to be accrued by the smart pool.
  function depositMP(
    uint256 maturity,
    address supplier,
    uint256 amount,
    uint256 minAmountRequired
  ) internal returns (uint256 currentTotalDeposit, uint256 earningsSP) {
    PoolLib.MaturityPool storage pool = maturityPools[maturity];

    earningsSP = pool.accrueEarnings(maturity, block.timestamp);

    (uint256 fee, uint256 feeSP) = interestRateModel.getYieldForDeposit(
      pool.smartPoolBorrowed(),
      pool.earningsUnassigned,
      amount
    );

    currentTotalDeposit = amount + fee;
    if (currentTotalDeposit < minAmountRequired) revert TooMuchSlippage();

    smartPoolBorrowed -= pool.deposit(amount);
    pool.earningsUnassigned -= fee + feeSP;
    smartPoolEarningsAccumulator += feeSP;

    // We update users's position
    PoolLib.Position memory position = mpUserSuppliedAmount[maturity][supplier];

    // If user doesn't have a current position, we add it to the list of all of them
    if (position.principal == 0) {
      userMpSupplied[supplier] = userMpSupplied[supplier].setMaturity(maturity);
    }

    mpUserSuppliedAmount[maturity][supplier] = PoolLib.Position(position.principal + amount, position.fee + fee);
  }

  /// @notice Accounts for withdrawing from a maturity pool.
  /// @param maturity maturity date / pool id where the asset should be accounted for.
  /// @param redeemer address that should have the assets withdrawn.
  /// @param positionAssets amount that the redeemer will be extracting from his position.
  /// @param minAmountRequired minimum amount that the supplier is expecting to withdraw.
  /// @param smartPoolAssets total assets in the smart pool.
  /// @return redeemAmountDiscounted amount of assets to be withdrawn (can include a discount for early withdraw).
  /// @return earningsSP amount of earnings to be accrued by the smart pool.
  function withdrawMP(
    uint256 maturity,
    address redeemer,
    uint256 positionAssets,
    uint256 minAmountRequired,
    uint256 smartPoolAssets
  ) internal returns (uint256 redeemAmountDiscounted, uint256 earningsSP) {
    PoolLib.MaturityPool storage pool = maturityPools[maturity];

    earningsSP = pool.accrueEarnings(maturity, block.timestamp);

    PoolLib.Position memory position = mpUserSuppliedAmount[maturity][redeemer];

    if (positionAssets > position.principal + position.fee) positionAssets = position.principal + position.fee;

    // We verify if there are any penalties/fee for him because of
    // early withdrawal - if so: discount
    if (block.timestamp < maturity) {
      updateSmartPoolAssetsAverage(smartPoolAssets);
      redeemAmountDiscounted = positionAssets.divWadDown(
        1e18 +
          interestRateModel.getRateToBorrow(
            maturity,
            block.timestamp,
            positionAssets,
            pool.borrowed,
            pool.supplied,
            smartPoolAssetsAverage
          )
      );
    } else {
      redeemAmountDiscounted = positionAssets;
    }

    if (redeemAmountDiscounted < minAmountRequired) revert TooMuchSlippage();

    // We remove the supply from the offer
    smartPoolBorrowed += pool.withdraw(
      PoolLib.Position(position.principal, position.fee).scaleProportionally(positionAssets).principal,
      smartPoolAssets - smartPoolBorrowed
    );

    // All the fees go to unassigned or to the smart pool
    (uint256 earningsUnassigned, uint256 newEarningsSP) = PoolLib.distributeEarningsAccordingly(
      positionAssets - redeemAmountDiscounted,
      pool.smartPoolBorrowed(),
      redeemAmountDiscounted
    );
    pool.earningsUnassigned += earningsUnassigned;
    smartPoolEarningsAccumulator += newEarningsSP;

    // the user gets discounted the full amount
    position.reduceProportionally(positionAssets);
    if (position.principal + position.fee == 0) {
      delete mpUserSuppliedAmount[maturity][redeemer];
      userMpSupplied[redeemer] = userMpSupplied[redeemer].clearMaturity(maturity);
    } else {
      // we proportionally reduce the values
      mpUserSuppliedAmount[maturity][redeemer] = position;
    }
  }

  /// @notice Accounts for repaying from a maturity pool.
  /// @param maturity maturity date / pool id where the asset should be accounted for.
  /// @param borrower address where the debt will be reduced.
  /// @param positionAssets the sum of principal and fees that this repayment covers.
  /// @param maxAmountAllowed maximum amount of debt that the user is willing to accept to be repaid.
  /// @return repayAmount the amount with discounts included that will finally be transferred.
  /// @return debtCovered the sum of principal and fees that this repayment covers.
  /// @return earningsSP amount of earnings to be accrued by the smart pool depositors.
  function repayMP(
    uint256 maturity,
    address borrower,
    uint256 positionAssets,
    uint256 maxAmountAllowed
  )
    internal
    returns (
      uint256 repayAmount,
      uint256 debtCovered,
      uint256 earningsSP
    )
  {
    PoolLib.MaturityPool storage pool = maturityPools[maturity];

    earningsSP = pool.accrueEarnings(maturity, block.timestamp);

    PoolLib.Position memory position = mpUserBorrowedAmount[maturity][borrower];

    debtCovered = Math.min(positionAssets, position.principal + position.fee);

    PoolLib.Position memory scaleDebtCovered = PoolLib.Position(position.principal, position.fee).scaleProportionally(
      debtCovered
    );

    // Early repayment allows you to get a discount from the unassigned earnings
    if (block.timestamp < maturity) {
      // We calculate the deposit fee considering the amount of debt the user'll pay
      (uint256 discountFee, uint256 feeSP) = interestRateModel.getYieldForDeposit(
        pool.smartPoolBorrowed(),
        pool.earningsUnassigned,
        scaleDebtCovered.principal
      );

      // We remove the fee from unassigned earnings
      pool.earningsUnassigned -= discountFee + feeSP;

      // The fee gets discounted from the user through `repayAmount`
      repayAmount = debtCovered - discountFee;

      // The fee charged to the MP supplier go to the smart pool accumulator
      smartPoolEarningsAccumulator += feeSP;
    } else {
      repayAmount = debtCovered + debtCovered.mulWadDown((block.timestamp - maturity) * penaltyRate);

      // All penalties go to the smart pool accumulator
      smartPoolEarningsAccumulator += repayAmount - debtCovered;
    }

    // We verify that the user agrees to this discount or penalty
    if (repayAmount > maxAmountAllowed) revert TooMuchSlippage();

    // We reduce the borrowed and we might decrease the SP debt
    smartPoolBorrowed -= pool.repay(scaleDebtCovered.principal);

    // We update the user position
    position.reduceProportionally(debtCovered);
    if (position.principal + position.fee == 0) {
      delete mpUserBorrowedAmount[maturity][borrower];
      userMpBorrowed[borrower] = userMpBorrowed[borrower].clearMaturity(maturity);
    } else {
      // We proportionally reduce the values
      mpUserBorrowedAmount[maturity][borrower] = position;
    }
  }

  /// @dev Gets all borrows for an account in certain maturity (or MATURITY_ALL).
  /// @param who account to return status snapshot in the specified maturity date.
  /// @param maturity maturity where the borrow is taking place. MATURITY_ALL returns all borrows.
  /// @return sumPositions the total amount of borrows in user position.
  /// @return sumPenalties the total penalties for late repayment in all maturities.
  function getAccountBorrows(address who, uint256 maturity)
    public
    view
    returns (uint256 sumPositions, uint256 sumPenalties)
  {
    if (maturity == PoolLib.MATURITY_ALL) {
      uint256 encodedMaturities = userMpBorrowed[who];
      uint256 baseMaturity = encodedMaturities % (1 << 32);
      uint256 packedMaturities = encodedMaturities >> 32;
      // We calculate all the timestamps using the baseMaturity and the following bits representing the following weeks
      for (uint256 i = 0; i < 224; ) {
        if ((packedMaturities & (1 << i)) != 0) {
          (uint256 position, uint256 penalties) = getAccountDebt(who, baseMaturity + (i * TSUtils.INTERVAL));
          sumPositions += position;
          sumPenalties += penalties;
        }
        unchecked {
          ++i;
        }
        if ((1 << i) > packedMaturities) break;
      }
    } else (sumPositions, sumPenalties) = getAccountDebt(who, maturity);
  }

  /// @notice Gets the debt + penalties of an account for a certain maturity.
  /// @param who account to return debt status for the specified maturity.
  /// @param maturity amount to be transferred.
  /// @return position the position debt denominated in number of tokens.
  /// @return penalties the penalties for late repayment.
  function getAccountDebt(address who, uint256 maturity) internal view returns (uint256 position, uint256 penalties) {
    PoolLib.Position memory data = mpUserBorrowedAmount[maturity][who];
    position = data.principal + data.fee;
    uint256 secondsDelayed = TSUtils.secondsPre(maturity, block.timestamp);
    if (secondsDelayed > 0) penalties = position.mulWadDown(secondsDelayed * penaltyRate);
  }

  /// @notice Updates the smartPoolAssetsAverage.
  /// @param smartPoolAssets smart pool total assets.
  function updateSmartPoolAssetsAverage(uint256 smartPoolAssets) internal {
    uint256 dampSpeedFactor = smartPoolAssets < smartPoolAssetsAverage ? dampSpeedDown : dampSpeedUp;
    uint256 averageFactor = uint256(1e18 - (-int256(dampSpeedFactor * (block.timestamp - lastAverageUpdate))).expWad());
    smartPoolAssetsAverage =
      smartPoolAssetsAverage.mulWadDown(1e18 - averageFactor) +
      averageFactor.mulWadDown(smartPoolAssets);
    lastAverageUpdate = uint32(block.timestamp);
  }
}

error AlreadyInitialized();
error TooMuchSlippage();
error SmartPoolReserveExceeded();
","keccak256":"0x6870be70372fc3a9cd63e48b9c009f4305bf4bf1e93c1471daedab5bc433b91e","license":"GPL-3.0-or-later"},"contracts/utils/PoolLib.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.13;

import { FixedPointMathLib } from "@rari-capital/solmate/src/utils/FixedPointMathLib.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { TSUtils } from "./TSUtils.sol";

library PoolLib {
  using FixedPointMathLib for uint256;

  /// @notice contains the accountability of a maturity.
  /// @param borrowed total amount borrowed from the maturity.
  /// @param supplied total amount supplied to the maturity.
  /// @param earningsUnassigned total amount of earnings not yet distributed and accrued.
  /// @param lastAccrual timestamp for the last time that some earnings have been distributed to earningsSP.
  struct MaturityPool {
    uint256 borrowed;
    uint256 supplied;
    uint256 earningsUnassigned;
    uint256 lastAccrual;
  }

  /// @notice contains principal and fee of a borrow or a supply position of a user in a maturity.
  /// @param principal amount borrowed or supplied to the maturity.
  /// @param fee amount of fees to be repaid or earned at maturity.
  struct Position {
    uint256 principal;
    uint256 fee;
  }

  uint256 public constant MATURITY_ALL = type(uint256).max;

  /// @notice calculates the amount that a maturity borrowed from the smart pool.
  /// @param pool maturity pool.
  /// @return amount borrowed from the maturity.
  function smartPoolBorrowed(MaturityPool storage pool) internal view returns (uint256) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    return borrowed - Math.min(borrowed, supplied);
  }

  /// @notice registers an operation to add supply to a maturity and potentially reduce smart pool debt.
  /// @param pool maturity pool where an amount will be added to the supply.
  /// @param amount amount to be added to the supply.
  /// @return smartPoolDebtReduction amount that will be reduced from the smart pool debt.
  function deposit(MaturityPool storage pool, uint256 amount) internal returns (uint256 smartPoolDebtReduction) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    pool.supplied = supplied + amount;
    smartPoolDebtReduction = Math.min(borrowed - Math.min(borrowed, supplied), amount);
  }

  /// @notice registers an operation to reduce borrowed amount from a maturity and potentially reduce smart pool debt.
  /// @param pool maturity where an amount will be repaid.
  /// @param amount amount to be added to the maturity.
  /// @return smartPoolDebtReduction amount that will be reduced from the smart pool debt.
  function repay(MaturityPool storage pool, uint256 amount) internal returns (uint256 smartPoolDebtReduction) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    pool.borrowed = borrowed - amount;
    smartPoolDebtReduction = Math.min(borrowed - Math.min(borrowed, supplied), amount);
  }

  /// @notice registers an operation to increase borrowed amount of a maturity and potentially increase smart pool debt.
  /// @param pool maturity where an amount will be borrowed.
  /// @param amount amount to be borrowed from the maturity.
  /// @return smartPoolDebtAddition amount of new debt that needs to be borrowed from the smart pool.
  function borrow(
    MaturityPool storage pool,
    uint256 amount,
    uint256 maxDebt
  ) internal returns (uint256 smartPoolDebtAddition) {
    uint256 borrowed = pool.borrowed;
    uint256 newBorrowed = borrowed + amount;
    uint256 oldSupply = Math.max(borrowed, pool.supplied);

    smartPoolDebtAddition = newBorrowed - Math.min(oldSupply, newBorrowed);

    if (smartPoolDebtAddition > maxDebt) revert InsufficientProtocolLiquidity();

    pool.borrowed = newBorrowed;
  }

  /// @notice registers an operation to reduce supply from a maturity and potentially increase smart pool debt.
  /// @param pool maturity where amount will be withdrawn.
  /// @param amountToDiscount amoun to be withdrawn from the maturity.
  /// @return smartPoolDebtAddition amount of new debt that needs to be borrowed from the smart pool.
  function withdraw(
    MaturityPool storage pool,
    uint256 amountToDiscount,
    uint256 maxDebt
  ) internal returns (uint256 smartPoolDebtAddition) {
    uint256 borrowed = pool.borrowed;
    uint256 supplied = pool.supplied;
    uint256 newSupply = supplied - amountToDiscount;

    smartPoolDebtAddition = Math.min(supplied, borrowed) - Math.min(newSupply, borrowed);

    if (smartPoolDebtAddition > maxDebt) revert InsufficientProtocolLiquidity();

    pool.supplied = newSupply;
  }

  /// @notice accrues smart pool earnings from earningsUnassigned based on the lastAccrual time.
  /// @param pool maturity where earnings will be accrued.
  /// @param currentTimestamp timestamp of the current transaction.
  /// @param maturity maturity date of the pool.
  /// @return earningsSP amount of earnings to be distributed to the smart pool.
  function accrueEarnings(
    MaturityPool storage pool,
    uint256 maturity,
    uint256 currentTimestamp
  ) internal returns (uint256 earningsSP) {
    uint256 lastAccrual = pool.lastAccrual;

    if (lastAccrual == maturity) return 0;

    // seconds from last accrual to the closest:
    // maturity date or the current timestamp
    uint256 secondsSinceLastAccrue = TSUtils.secondsPre(lastAccrual, Math.min(maturity, currentTimestamp));
    // seconds from last accrual to the maturity date
    uint256 secondsTotalToMaturity = TSUtils.secondsPre(lastAccrual, maturity);
    pool.lastAccrual = Math.min(maturity, currentTimestamp);

    // assign some of the earnings to be collected at maturity
    uint256 earningsUnassigned = pool.earningsUnassigned;
    earningsSP = earningsUnassigned.mulDivDown(secondsSinceLastAccrue, secondsTotalToMaturity);
    pool.earningsUnassigned = earningsUnassigned - earningsSP;
  }

  /// @notice modify positions based on a certain amount, keeping the original principal/fee ratio.
  /// @dev modifies the original struct and returns it. Needs for the amount to be less than the principal and the fee
  /// @param position original position to be scaled.
  /// @param amount to be used as a full value (principal + interest).
  /// @return Position scaled position.
  function scaleProportionally(Position memory position, uint256 amount) internal pure returns (Position memory) {
    uint256 principal = amount.mulDivDown(position.principal, position.principal + position.fee);
    position.principal = principal;
    position.fee = amount - principal;
    return position;
  }

  /// @notice reduce positions based on a certain amount, keeping the original principal/fee ratio.
  /// @dev modifies the original struct and returns it.
  /// @param position original position to be reduced.
  /// @param amount to be used as a full value (principal + interest).
  /// @return Position reduced position.
  function reduceProportionally(Position memory position, uint256 amount) internal pure returns (Position memory) {
    uint256 principal = amount.mulDivDown(position.principal, position.principal + position.fee);
    position.principal -= principal;
    position.fee -= amount - principal;
    return position;
  }

  /// @notice calculates what proportion of earnings would amountFunded represent considering suppliedSP.
  /// @param earnings amount to be distributed.
  /// @param suppliedSP amount that the maturity borrowed from the smart pool.
  /// @param amountFunded amount that will be checked if came from the smart pool or maturity.
  /// @return earningsUnassigned earnings to be added to earningsUnassigned.
  /// @return earningsSP earnings to be distributed to the smart pool.
  function distributeEarningsAccordingly(
    uint256 earnings,
    uint256 suppliedSP,
    uint256 amountFunded
  ) internal pure returns (uint256 earningsUnassigned, uint256 earningsSP) {
    earningsSP = amountFunded == 0
      ? 0
      : earnings.mulDivDown(amountFunded - Math.min(suppliedSP, amountFunded), amountFunded);
    earningsUnassigned = earnings - earningsSP;
  }

  /// @notice adds a maturity date to the borrow or supply positions of the user.
  /// @param encoded encoded maturity dates where the user borrowed or supplied to.
  /// @param maturity the new maturity where the user will borrow or supply to.
  /// @return updated encoded maturity dates.
  function setMaturity(uint256 encoded, uint256 maturity) internal pure returns (uint256) {
    // we initialize the maturity date with also the 1st bit on the 33th position set
    if (encoded == 0) return maturity | (1 << 32);

    uint256 baseMaturity = encoded % (1 << 32);
    if (maturity < baseMaturity) {
      // If the new maturity date is lower than the base, then we need to set it as the new base. We wipe clean the
      // last 32 bits, we shift the amount of INTERVALS and we set the new value with the 33rd bit set
      uint256 range = (baseMaturity - maturity) / TSUtils.INTERVAL;
      if (encoded >> (256 - range) != 0) revert MaturityOverflow();
      encoded = ((encoded >> 32) << (32 + range));
      return maturity | encoded | (1 << 32);
    } else {
      uint256 range = (maturity - baseMaturity) / TSUtils.INTERVAL;
      if (range > 223) revert MaturityOverflow();
      return encoded | (1 << (32 + range));
    }
  }

  /// @notice remove maturity from user's borrow or supplied positions.
  /// @param encoded encoded maturity dates where the user borrowed or supplied to.
  /// @param maturity maturity date to be removed.
  /// @return updated encoded maturity dates.
  function clearMaturity(uint256 encoded, uint256 maturity) internal pure returns (uint256) {
    if (encoded == 0 || encoded == maturity | (1 << 32)) return 0;

    uint256 baseMaturity = encoded % (1 << 32);
    // if the baseMaturity is the one being cleaned
    if (maturity == baseMaturity) {
      // We're wiping 32 bytes + 1 for the old base flag
      uint256 packed = encoded >> 33;
      uint256 range = 1;
      while ((packed & 1) == 0 && packed != 0) {
        unchecked {
          ++range;
        }
        packed >>= 1;
      }
      encoded = ((encoded >> (32 + range)) << 32);
      return (maturity + (range * TSUtils.INTERVAL)) | encoded;
    } else {
      // otherwise just clear the bit
      return encoded & ~(1 << (32 + ((maturity - baseMaturity) / TSUtils.INTERVAL)));
    }
  }

  /// @notice checks if the user has positions in a maturity date.
  /// @param encoded encoded maturity dates where the user borrowed or supplied to.
  /// @param maturity maturity date.
  /// @return true if the user has positions in the maturity date.
  function hasMaturity(uint256 encoded, uint256 maturity) internal pure returns (bool) {
    uint256 baseMaturity = encoded % (1 << 32);
    if (maturity < baseMaturity) return false;

    uint256 range = (maturity - baseMaturity) / TSUtils.INTERVAL;
    if (range > 223) return false;
    return ((encoded >> 32) & (1 << range)) != 0;
  }
}

error InsufficientProtocolLiquidity();
error MaturityOverflow();
","keccak256":"0xa0220781c09053ff887bc20abf4cbb3b1334ab36ecbbea60ba2358d7a703064c","license":"GPL-3.0-or-later"},"contracts/utils/TSUtils.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.13;

library TSUtils {
  enum State {
    NONE,
    INVALID,
    MATURED,
    VALID,
    NOT_READY
  }

  uint32 public constant INTERVAL = 4 weeks;

  /// @notice calculates how many seconds are left to a certain date.
  /// @param timestampFrom to calculate the difference in seconds from a date.
  /// @param timestampTo to calculate the difference in seconds to a date.
  /// @return seconds left to the date.
  function secondsPre(uint256 timestampFrom, uint256 timestampTo) internal pure returns (uint256) {
    return timestampFrom < timestampTo ? timestampTo - timestampFrom : 0;
  }

  /// @notice returns a pool `time` state based on the current time, maxPools available, and INTERVAL.
  /// @param timestamp timestamp of the current time.
  /// @param maturity used as maturity date / pool id.
  /// @param maxPools number of pools available in the time horizon.
  /// @return state: if a pool is VALID, not yet available(NOT_READY), INVALID or MATURED.
  function getPoolState(
    uint256 timestamp,
    uint256 maturity,
    uint8 maxPools
  ) private pure returns (State) {
    if (maturity % INTERVAL != 0) return State.INVALID;

    if (maturity <= timestamp) return State.MATURED;

    if (maturity > timestamp - (timestamp % INTERVAL) + (INTERVAL * maxPools)) return State.NOT_READY;

    return State.VALID;
  }

  /// @notice verifies that a maturity is VALID, MATURED, NOT_READY or INVALID.
  /// @dev if expected state doesn't match the calculated one, it reverts with a custom error "UnmatchedPoolState".
  /// @param maturity timestamp of the maturity date to be verified.
  /// @param requiredState state required by the caller to be verified (see TSUtils.State() for description).
  /// @param alternativeState state required by the caller to be verified (see TSUtils.State() for description).
  function validateRequiredPoolState(
    uint8 maxFuturePools,
    uint256 maturity,
    State requiredState,
    State alternativeState
  ) internal view {
    State poolState = getPoolState(block.timestamp, maturity, maxFuturePools);

    if (poolState != requiredState && poolState != alternativeState) {
      if (alternativeState == State.NONE) revert UnmatchedPoolState(uint8(poolState), uint8(requiredState));

      revert UnmatchedPoolStateMultiple(uint8(poolState), uint8(requiredState), uint8(alternativeState));
    }
  }
}

error UnmatchedPoolState(uint8 state, uint8 requiredState);
error UnmatchedPoolStateMultiple(uint8 state, uint8 requiredState, uint8 alternativeState);
